(function(){'use strict';const STORAGE_KEY = 'crex-theme';const THEME_LIGHT = 'light';const THEME_DARK = 'dark';const THEME_AUTO = 'auto';let currentPreference = THEME_AUTO;let systemPreference = null;function detectSystemPreference(){if(window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches){return THEME_DARK;}return THEME_LIGHT;}function getStoredPreference(){try{return localStorage.getItem(STORAGE_KEY)|| THEME_AUTO;}catch(e){return THEME_AUTO;}}function savePreference(theme){try{localStorage.setItem(STORAGE_KEY,theme);currentPreference = theme;}catch(e){console.warn('Impossible de sauvegarder la pr√©f√©rence:',e);}}function getEffectiveTheme(){if(currentPreference === THEME_AUTO){return detectSystemPreference();}return currentPreference;}function applyTheme(theme){const effectiveTheme = theme === THEME_AUTO ? detectSystemPreference(): theme;const html = document.documentElement;const body = document.body;if(body && !body.classList.contains('theme-transition-ready')){body.classList.add('theme-transition-ready');}html.classList.remove('dark-mode','light-mode');if(body){body.classList.remove('dark-mode','light-mode');}html.setAttribute('data-theme',effectiveTheme);if(effectiveTheme === THEME_DARK){html.classList.add('dark-mode');if(body)body.classList.add('dark-mode');}else{html.classList.add('light-mode');if(body)body.classList.add('light-mode');}updateToggleButton(effectiveTheme);document.dispatchEvent(new CustomEvent('themechange',{detail:{theme: effectiveTheme,preference: currentPreference}}));void html.offsetHeight;}function updateToggleButton(theme){const toggles = document.querySelectorAll('.theme-toggle,[data-theme-toggle]');toggles.forEach(toggle =>{if(!toggle)return;const icon = toggle.querySelector('.theme-icon,[data-theme-icon]');if(theme === THEME_DARK){toggle.classList.add('active');toggle.setAttribute('aria-label','Activer le mode clair');toggle.setAttribute('aria-pressed','true');if(icon){icon.textContent = '‚òÄÔ∏è';icon.className = 'theme-icon sun-icon';}else{toggle.innerHTML = '<span class="theme-icon sun-icon">‚òÄÔ∏è</span>';}}else{toggle.classList.remove('active');toggle.setAttribute('aria-label','Activer le mode sombre');toggle.setAttribute('aria-pressed','false');if(icon){icon.textContent = 'üåô';icon.className = 'theme-icon moon-icon';}else{toggle.innerHTML = '<span class="theme-icon moon-icon">üåô</span>';}}});}function setTheme(theme){if([THEME_LIGHT,THEME_DARK,THEME_AUTO].includes(theme)){savePreference(theme);applyTheme(theme);}}function toggleTheme(){const effectiveTheme = getEffectiveTheme();const newTheme = effectiveTheme === THEME_DARK ? THEME_LIGHT : THEME_DARK;setTheme(newTheme);}function applyThemeImmediate(){const html = document.documentElement;const savedTheme = getStoredPreference();const theme = savedTheme === THEME_AUTO ? detectSystemPreference(): savedTheme;html.classList.remove('dark-mode','light-mode');html.setAttribute('data-theme',theme);if(theme === THEME_DARK){html.classList.add('dark-mode');}else{html.classList.add('light-mode');}}function init(){applyThemeImmediate();currentPreference = getStoredPreference();systemPreference = detectSystemPreference();applyTheme(currentPreference);if(window.matchMedia){const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');const handleSystemChange =(e)=>{systemPreference = e.matches ? THEME_DARK : THEME_LIGHT;if(currentPreference === THEME_AUTO){applyTheme(THEME_AUTO);}};if(mediaQuery.addEventListener){mediaQuery.addEventListener('change',handleSystemChange);}else{mediaQuery.addListener(handleSystemChange);}}document.addEventListener('click',function(e){const toggle = e.target.closest('.theme-toggle,[data-theme-toggle]');if(toggle){e.preventDefault();e.stopPropagation();toggleTheme();}});window.CRExTheme ={setTheme: setTheme,toggle: toggleTheme,getTheme:()=> currentPreference,getEffectiveTheme: getEffectiveTheme,THEME_LIGHT: THEME_LIGHT,THEME_DARK: THEME_DARK,THEME_AUTO: THEME_AUTO};}if(document.documentElement){applyThemeImmediate();}if(document.readyState === 'loading'){document.addEventListener('DOMContentLoaded',init);}else{setTimeout(init,10);}})();